import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { CustomerService } from '../customer.service';

@Component({
  selector: 'app-create',
  templateUrl: './create.component.html',
  styleUrls: ['./create.component.css']
})
export class CreateComponent implements OnInit {

  issueForm!: FormGroup;
  AssetsList: any = [];
  CountryList: any = [];
  RegionList: any = [];
  CityList: any = [];
  LocationList: any = [];

  constructor( public fb: FormBuilder,  public customerService: CustomerService,
     private router: Router) { }

  ngOnInit(): void {
    this.loadmasters();

    this.issueForm = this.fb.group({
      CustomerName: new FormControl('', [Validators.required]),
      AssetId: new FormControl('', Validators.required),     
      CountryId: new FormControl('', Validators.required),     
      RegionId: new FormControl('', Validators.required), 
      CityId: new FormControl('', Validators.required), 
      LocationId: new FormControl('', Validators.required), 
    });
  }


  get f(){
    return this.issueForm.controls;
  }

  /**
   * Write code on Method
   *
   * @return response()
   */
  submit(){
    console.log(this.issueForm.value);
    this.customerService.create(this.issueForm.value).subscribe((res:any) => {
         console.log('customer created successfully!');
         this.router.navigateByUrl('customer/index');
    })
  }

loadmasters()
{
    this.loadassets();
    this.loadcountries();
   // this.loadregions();
    this.loadcity();
    this.loadlocations();
}

  loadassets() {
    return this.customerService.getAllAssets().subscribe((data: {}) => {    
      this.AssetsList = data;
    })
  }

  loadcountries() {
    return this.customerService.getAllCountries().subscribe((data: {}) => {    
      this.CountryList = data;
    })
  }

  // loadregions() {
  //   return this.customerService.getAllRegions().subscribe((data: {}) => {    
  //     this.RegionList = data;
  //   })
  // }

  loadcity() {
    return this.customerService.getAllCities().subscribe((data: {}) => {        
      this.CityList = data;
    })
  }

  loadlocations() {
    return this.customerService.getAllLocations().subscribe((data: {}) => {    
      this.LocationList = data;
    })
  }
  changeCountry() {   
    return this.customerService.getAllRegions(Number(this.issueForm.value.CountryId)).subscribe((data: {}) => {    
      this.RegionList =data;  
    })     
  }
}
<div class="container">
    <h4>Create New Customer</h4>  
    <a href="#" routerLink="/customer/index" class="btn btn-primary">Back</a>        
      <form [formGroup]="issueForm" (ngSubmit)="submit()">
        <table class="table table-striped">
            <tr>   
                <td> 
                 <label for="CustomerName">Customer Name:</label> 
                </td>
                <td>  
                    <input 
                    formControlName="CustomerName"
                    id="CustomerName" 
                    type="text" 
                    class="form-control">
                <div *ngIf="f['CustomerName'].touched && f['CustomerName'].invalid" class="alert alert-danger">
                <div *ngIf="f['CustomerName'].errors && f['CustomerName'].errors['required']">CustomerName is required.</div>
                </div> 
                </td> 
            </tr>

            <tr>   
                <td> 
                    <label for="AssetId">Asset Type:</label>
                </td>
                <td>  
                    <select formControlName="AssetId" class="form-control">
                        <option disabled>Select AssetId</option>                
                        <option *ngFor="let asset of AssetsList" 
                        [value]="asset.AssetId">{{asset.AssetName}}</option>
                    </select>   
                </td> 
            </tr>
            <tr>   
                <td> 
                    <label for="CountryId">Country:</label>
                </td>
                <td>  
                    <select formControlName="CountryId"  
                    class="form-control"  (change)="changeCountry()">
                        <option disabled>Select Country</option>                
                        <option *ngFor="let country of CountryList" 
                        [value]="country.CountryId">{{country.CountryName}}</option>
                    </select>  
                </td> 
            </tr>
            <tr>   
                <td> 
                    <label for="RegionId">Region:</label>
                </td>
                <td>  
                    <select formControlName="RegionId" class="form-control">
                        <option disabled>Select Region</option>                
                        <option *ngFor="let region of RegionList" 
                        [value]="region.RegionId">{{region.RegionName}}</option>
                    </select>     
                </td> 
            </tr>
            <tr>   
                <td> 
                    <label for="CityId">City:</label>
                </td>
                <td>  
                    <select formControlName="CityId" class="form-control">
                        <option disabled>Select City</option>                
                        <option *ngFor="let city of CityList" 
                        [value]="city.CityId">{{city.CityName}}</option>
                    </select>   
                </td> 
            </tr>
            <tr>   
                <td> 
                    <label for="LocationId">Location:</label>
                </td>
                <td>  
                    <select formControlName="LocationId" class="form-control">
                        <option disabled>Select Location</option>                
                        <option *ngFor="let location of LocationList" 
                        [value]="location.LocationId">{{location.LocationName}}</option>
                    </select>   
                </td> 
            </tr>
            <tr>   
                <td>                     
                </td>
                <td>  
                    <button class="btn btn-primary" type="submit" 
                    [disabled]="!issueForm.valid">Submit</button>
                </td> 
            </tr>
        </table>              
     </form>  
</div>
