import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { CustomerService } from '../customer.service';

@Component({
  selector: 'app-create',
  templateUrl: './create.component.html',
  styleUrls: ['./create.component.css']
})
export class CreateComponent implements OnInit {

  issueForm!: FormGroup;
  AssetsList: any = [];
  CountryList: any = [];
  RegionList: any = [];
  CityList: any = [];
  LocationList: any = [];

  constructor( public fb: FormBuilder,  public customerService: CustomerService,
     private router: Router) { }

  ngOnInit(): void {
    this.loadmasters();

    this.issueForm = this.fb.group({
      customerName: new FormControl('', [Validators.required]),
      lastName: new FormControl('', Validators.required),
      gender: new FormControl('', Validators.required),
      dob: new FormControl('', Validators.required),
      departmentId: new FormControl('', Validators.required),
    });
  }


  get f(){
    return this.issueForm.controls;
  }

  /**
   * Write code on Method
   *
   * @return response()
   */
  submit(){
    console.log(this.issueForm.value);
    this.customerService.create(this.issueForm.value).subscribe((res:any) => {
         console.log('customer created successfully!');
         this.router.navigateByUrl('customer/index');
    })
  }

loadmasters()
{
    this.loadassets();
    this.loadcountries();
    this.loadregions();
    this.loadcity();
    this.loadlocations();
}

  loadassets() {
    return this.customerService.getAllAssets().subscribe((data: {}) => {    
      this.AssetsList = data;
    })
  }

  loadcountries() {
    return this.customerService.getAllCountries().subscribe((data: {}) => {    
      this.CountryList = data;
    })
  }

  loadregions() {
    return this.customerService.getAllRegions().subscribe((data: {}) => {    
      this.RegionList = data;
    })
  }

  loadcity() {
    return this.customerService.getAllCities().subscribe((data: {}) => {    
      this.CityList = data;
    })
  }

  loadlocations() {
    return this.customerService.getAllLocations().subscribe((data: {}) => {    
      this.LocationList = data;
    })
  }

}
<div class="container">
    <h4>Create New Employee</h4>  
    <a href="#" routerLink="/customer/index" class="btn btn-primary">Back</a>        
      <form [formGroup]="issueForm" (ngSubmit)="submit()">
        <div class="form-group">
            <label for="customerName">Customer Name:</label>
            <input 
                formControlName="customerName"
                id="customerName" 
                type="text" 
                class="form-control">
            <div *ngIf="f['customerName'].touched && f['customerName'].invalid" class="alert alert-danger">
                <div *ngIf="f['customerName'].errors && f['customerName'].errors['required']">customerName is required.</div>
            </div>
        </div>
     </form>  
</div>
