import { Component, OnInit } from '@angular/core'; 
import { ActivatedRoute, Router } from '@angular/router'; 
import { FormGroup, FormControl, Validators, FormBuilder} from '@angular/forms';
import { CustomerService } from '../customer.service';
import { Customer } from '../customer';

@Component({
  selector: 'app-edit',
  templateUrl: './edit.component.html',
  styleUrls: ['./edit.component.css']
})
export class EditComponent implements OnInit {


  id!: number;
  cust!: Customer;
  form!: FormGroup;  

  AssetsList: any = [];
  CountryList: any = [];
  RegionList: any = [];
  CityList: any = [];
  LocationList: any = [];

  constructor(public fb: FormBuilder,
    public customerService: CustomerService,
    private route: ActivatedRoute,
    private router: Router
  ) { }

  /**
   * Write code on Method
   *
   * @return response()
   */
  ngOnInit(): void {   
    this.id = Number(this.route.snapshot.params['postId']);
    this.customerService.find(this.id).subscribe((data: Customer)=>{     
      this.cust = data;
    }); 

    this.form = this.fb.group({
      CustomerName: new FormControl('', [Validators.required]),
      AssetId: new FormControl('', Validators.required),     
      CountryId: new FormControl('', Validators.required),     
      RegionId: new FormControl('', Validators.required), 
      CityId: new FormControl('', Validators.required), 
      LocationId: new FormControl('', Validators.required), 
    });
  }

  get f(){
    return this.form.controls;
  }

  /**
   * Write code on Method
   *
   * @return response()
   */
  submit(){
    console.log(this.form.value);
    this.customerService.update(this.form.value).subscribe((res:any) => {
         console.log('Post updated successfully!');
         this.router.navigateByUrl('employee/index');
    })
  }

  loadmasters()
  {
      this.loadassets();
      this.loadcountries();  
  }

    loadassets() {
      return this.customerService.getAllAssets().subscribe((data: {}) => {    
        this.AssetsList = data;
      })
    }

    loadcountries() {
      return this.customerService.getAllCountries().subscribe((data: {}) => {    
        this.CountryList = data;
      })
    }


    changeCountry() {   
      return this.customerService.getAllRegions(Number(this.form.value.CountryId)).subscribe((data: {}) => {    
        this.RegionList =data;  
      })     
    }

    changeRegion() {   
      return this.customerService.getAllCities(Number(this.form.value.CountryId),Number(this.form.value.RegionId)).subscribe((data: {}) => {    
        this.CityList =data;  
      })     
    }

    changeCity() {   
      return this.customerService.getAllLocations(Number(this.form.value.CountryId),Number(this.form.value.RegionId),Number(this.form.value.CityId)).subscribe((data: {}) => {    
        this.LocationList  =data;  
      })     
    }
}
