import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';

import {  Observable, throwError } from 'rxjs';
import { catchError, retry } from 'rxjs/operators';

import { Customer } from './customer';

@Injectable({
  providedIn: 'root'
})
export class CustomerService {
  private apiURL = "http://localhost:54833/api/customer";  

  httpOptions = {
    headers: new HttpHeaders({
      'Content-Type': 'application/json',
      'Access-Control-Allow-Origin': '*',
      'Access-Control-Allow-Headers': 'Origin, X-Requested-With, Content-Type, Accept'
    })
  }

  constructor(private httpClient: HttpClient) { }

  // getAll(): Observable<Customer[]> {
  //   return this.httpClient.get<Customer[]>(this.apiURL + '/getallcustomers')
  //   .pipe(
  //     catchError(this.errorHandler)
  //   )
  // } 
      /**
   * Write code on Method
   *
   * @return response()
   */
  getAll(): Observable<Customer> {
    var result=this.httpClient.get('https://localhost/getdata');
    console.log(result);
    debugger;
    return this.httpClient
      .get<Customer>(this.apiURL + '/getallcustomers')
      .pipe(retry(1), catchError(this.errorHandler));
  }

  errorHandler(error: { error: { message: string; }; status: any; message: any; }) {
    let errorMessage = '';
    if(error.error instanceof ErrorEvent) {
      errorMessage = error.error.message;
    } else {
      errorMessage = `Error Code: ${error.status}\nMessage: ${error.message}`;
    }
    return throwError(errorMessage);
 }
}
